mapper.select:
select

from mall_product
where is_del = 0

  and name like concat('%', #{name}, '%')


  and model like concat('%', #{model}, '%')


  and category_id = #{categoryId}


  and brand_id = #{brandId}


  and product_group_id = #{productGroupId}

order by id desc
limit #{limit}

select

from mall_product
where is_del = 0

  and id < #{cursorId}


  and name like concat('%', #{name}, '%')


  and model like concat('%', #{model}, '%')


  and category_id = #{categoryId}


  and brand_id = #{brandId}


  and product_group_id = #{productGroupId}

order by id desc
limit #{limit}
